export const initialState = {
    isLoggedIn: false,
    user: null
}

export const LOG_IN = 'LOG_IN' // 액션의 이름
export const LOG_OUT = 'LOG_OUT' // 액션의 이름

export const loginAction = {
    type: LOG_IN,
    data: {
        nickname: '제로초',
        apple: '사과',
    }
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN: {
            return {
                ...state,
                isLoggedIn: true,
                user: action.data
            }
        }
        case LOG_OUT: {
            return {
                ...state,
                isLoggedIn: false,
                user: null
            }
        }
        default: {
            return {
                ...state
            }
        }
    }
}

export default reducer
